import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AppServiceService } from 'src/app/service/app-service.service';
@Component({
  selector: 'app-login-register',
  templateUrl: './login-register.component.html',
  styleUrls: ['./login-register.component.scss'],
})
export class LoginRegisterComponent implements OnInit {
  constructor(
    private appServiceService: AppServiceService,
    private router: Router
  ) {}
  loginForm = new FormGroup({
    username: new FormControl('', [Validators.required]),
    password: new FormControl('', [Validators.required]),
  });
  registerForm = new FormGroup({
    username: new FormControl('', [Validators.required]),
    password: new FormControl('', [Validators.required]),
    confirmPassword: new FormControl('', [Validators.required]),
  });
  pageTitle: any;
  ngOnInit(): void {
    {
      const stateData = window.history.state;
      if (stateData) {
        this.pageTitle = stateData.pageTitle;
        console.log(this.pageTitle); // { userId: 123, score: 50 }
      } else {
        console.log('No state data found.');
      }
    }
  }
  verifyUser() {
    let formValue = this.loginForm.value;

    if (
      formValue.username == 'Karthik Devendra Rao' &&
      formValue.password == 'Karthik@123'
    ) {
      alert('Login Successfully done !!');
      this.appServiceService.userDetail = formValue;
      this.router.navigate(['home']);
      //this.appServiceService.loginDone.next('done');
    } else {
      alert('Invalid username or Password !!');
    }
  }
  goToSignUp() {
    const data = { pageTitle: 'sign-up' };

    this.router.navigate(['/sign_up'], {
      state: data, // Passing data
    });
  }
}
<div class="login-container">
  <h2>Login</h2>
  <form
    [formGroup]="loginForm"
    id="loginForm"
    class="login-register-Form"
    (ngSubmit)="verifyUser()"
  >
    <!-- <label for="username">Username:</label> -->
    <input
      formControlName="username"
      placeholder="Username"
      type="text"
      id="username"
      required
    />

    <!-- <label for="password">Password:</label> -->
    <input
      formControlName="password"
      placeholder="Password"
      type="password"
      id="password"
      required
    />

    <button type="submit" [disabled]="loginForm.invalid">Login</button>
  </form>
  <p class="sign_up">
    Don't have an account? <button (click)="goToSignUp()">Sign up</button>
  </p>
  <p id="message"></p>
</div>
