import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { SudokuGeneratorService } from 'src/app/service/sudoku-generator.service';

@Component({
  selector: 'app-sudoku-grid',
  templateUrl: './sudoku-grid.component.html',
  styleUrls: ['./sudoku-grid.component.scss'],
})
export class SudokuGridComponent implements OnInit {
  //grid: any[][];
  @Input() grid: any[][] = [];
  @Output() VerifyPuzzle = new EventEmitter<any>();
  answergrid: any[] = [];
  selectedValue: any = null;
  valueCount: any[] = [
    { count: 0 },
    { count: 0 },
    { count: 0 },
    { count: 0 },
    { count: 0 },
    { count: 0 },
    { count: 0 },
    { count: 0 },
    { count: 0 },
  ];

  constructor(private sudokuGeneratorService: SudokuGeneratorService) {
    // this.grid = this.sudokuGeneratorService.generateSudoku();
  }
  ngOnInit() {
    this.answergrid = this.sudokuGeneratorService.generatePuzzleWith25Clues(
      this.grid
    );
    this.updateCounter();
  }

  validateInput(event: Event, row: number, col: number) {
    const input = event.target as HTMLInputElement;
    const value = input.value;

    if (!/^[1-9]$/.test(value)) {
      input.value = '';
      this.answergrid[row][col] = '';
    } else {
      this.answergrid[row][col] = value;
    }
    this.updateCounter();
  }
  comparetheGrid() {
    for (let i = 0; i < 9; i++) {
      for (let j = 0; j < 9; j++) {
        if (this.answergrid[i][j] != this.grid[i][j]) return false;
      }
    }
    return true;
  }
  Validate() {
    this.VerifyPuzzle.emit(this.comparetheGrid());
  }
  SelectedValue(value: any) {
    if (value != '') {
      this.selectedValue = value;
    } else {
      this.selectedValue = null;
    }
  }
  checkType(value: any) {
    return typeof value == 'number';
  }
  updateCounter() {
    let count = 0;
    for (let i = 0; i < this.valueCount.length; i++) {
      let count = 0;
      for (let row of this.answergrid) {
        for (let num of row) {
          if (num === i + 1) {
            count++;
          }
        }
      }
      this.valueCount[i].count = count;
    }

    // return count;
  }
}
<div class="sudoku-grid">
  <div *ngFor="let row of grid; let i = index" class="row">
    <input
      *ngFor="let col of row; let j = index"
      type="text"
      [(ngModel)]="answergrid[i][j]"
      [ngClass]="{
        selcted: selectedValue == answergrid[i][j],

      }"
      maxlength="1"
      pattern="[1-9]"
      (click)="SelectedValue(answergrid[i][j])"
      (input)="validateInput($event, i, j)"
      [readonly]="checkType(answergrid[i][j])"
    />
  </div>
</div>
<div class="counter-container">
  <div class="sudoku-value-grid">
    <div *ngFor="let row of valueCount; let i = index" class="value-row">
      <span class="number">
        {{ i + 1 }}
      </span>
      <span>
        {{ row.count }}
      </span>
    </div>
  </div>
</div>

<button (click)="Validate()">Verify Suduku</button>
