import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { SudokuGeneratorService } from 'src/app/service/sudoku-generator.service';

@Component({
  selector: 'app-sudoku-grid',
  templateUrl: './sudoku-grid.component.html',
  styleUrls: ['./sudoku-grid.component.scss'],
})
export class SudokuGridComponent implements OnInit {
  //grid: any[][];
  @Input() grid: any[][] = [];
  @Output() VerifyPuzzle = new EventEmitter<any>();
  answergrid: any[] = [];
  selectedValue: any = null;
  enableValueCount: boolean = false;
  selectedRow: any = null;
  selectedCol: any = null;
  wrongSelection: boolean = false;
  mistakeCount = 0;
  valueCount: any[] = [
    { count: 0 },
    { count: 0 },
    { count: 0 },
    { count: 0 },
    { count: 0 },
    { count: 0 },
    { count: 0 },
    { count: 0 },
    { count: 0 },
  ];

  constructor(private sudokuGeneratorService: SudokuGeneratorService) {
    // this.grid = this.sudokuGeneratorService.generateSudoku();
  }
  ngOnInit() {
    this.answergrid = this.sudokuGeneratorService.generatePuzzleWith25Clues(
      this.grid
    );
    this.updateCounter();
  }

  validateInput(event: Event, row: number, col: number) {
    const input = event.target as HTMLInputElement;
    const value = input.value;

    if (!/^[1-9]$/.test(value)) {
      input.value = '';
      this.answergrid[row][col] = '';
    } else {
      this.answergrid[row][col] = value;
    }
    this.updateCounter();
  }
  comparetheGrid() {
    for (let i = 0; i < 9; i++) {
      for (let j = 0; j < 9; j++) {
        if (this.answergrid[i][j] != this.grid[i][j]) return false;
      }
    }
    return true;
  }
  Validate() {
    this.VerifyPuzzle.emit(this.comparetheGrid());
  }
  SelectedValue(i: any, j: any) {
    if (this.checkType(this.answergrid[i][j])) {
      this.selectedValue = this.answergrid[i][j];
      this.enableValueCount = false;
      return;
    }
    if (this.answergrid[i][j] != '') {
      this.selectedRow = i;
      this.selectedCol = j;
      this.selectedValue = this.answergrid[i][j];
      this.enableValueCount = true;
    } else {
      this.selectedValue = null;
      this.selectedRow = i;
      this.selectedCol = j;
      this.enableValueCount = true;
    }
  }
  addValue(value: any) {
    let i = this.selectedRow;
    let j = this.selectedCol;
    this.answergrid[i][j] = String(value);
    if (i == null && j == null) return;
    if (this.answergrid[i][j] != this.grid[i][j]) {
      this.wrongSelection = true;
    } else {
      this.enableValueCount = false;
      this.selectedCol = null;
      this.selectedRow = null;
      this.wrongSelection = false;
      this.updateCounter();
    }
  }
  checkType(value: any) {
    return typeof value == 'number';
  }
  updateCounter() {
    let count = 0;
    for (let i = 0; i < this.valueCount.length; i++) {
      let count = 0;
      for (let row of this.answergrid) {
        for (let num of row) {
          if (num == i + 1) {
            count++;
          }
        }
      }
      this.valueCount[i].count = count;
    }

    // return count;
  }
}
<div class="message-section">
  <span>Mistakes: {{ mistakeCount }}/3 </span>
  <span>Score: {{ mistakeCount }} </span>
  <span>Time: {{ mistakeCount }}/3 </span>
</div>
<div class="sudoku-grid">
  <div *ngFor="let row of grid; let i = index" class="row">
    <input
      *ngFor="let col of row; let j = index"
      type="text"
      [(ngModel)]="answergrid[i][j]"
      [ngClass]="{
        'selcted-value': selectedValue == answergrid[i][j],
        'wrong-selection':
          wrongSelection && i == selectedRow && j == selectedCol
      }"
      maxlength="1"
      pattern="[1-9]"
      (click)="SelectedValue(i, j)"
      (input)="validateInput($event, i, j)"
      [readonly]="true"
    />
    <!-- [readonly]="checkType(answergrid[i][j])"
    /> -->
  </div>
</div>
<div class="counter-container">
  <div class="sudoku-value-grid">
    <div
      *ngFor="let row of valueCount; let i = index"
      class="value-row"
      [ngClass]="{
        'hide-number': 9 - row.count == 0,
        'disable-selection': !enableValueCount
      }"
      (click)="addValue(i + 1)"
    >
      <span class="number">
        {{ i + 1 }}
      </span>
      <span>
        {{ 9 - row.count }}
      </span>
    </div>
  </div>
</div>

<button (click)="Validate()">Verify Suduku</button>
